#include <stdio.h>
#include <stdlib.h>
#define SIZE 100
char lukey[] = "";
//char xploshi[] = "";
//char jam[] = "";

char canvas[SIZE][SIZE]; 

void createCanvas(int size){
	int i;
	int g;
	for (i=0; i<size;i++){
		for (g=0; g<size;g++){
				canvas[i][g] = 0;
		}
		putchar(i);
	}
}

void plot(int pointX, int pointY, int val){
	canvas[pointX % SIZE][pointY % SIZE] = val;
}

void line(int pointXA, int pointYA, int pointXB, int pointYB, int val){
    for (float t=0.; t<1.; t+=.01) { 
        int x = pointXA + (pointXB-pointXA)*t; 
        int y = pointYA + (pointYB-pointYA)*t; 
        plot(x, y, val); 
    } 
}

void renderCanvas(){
	int i;
	int g;
	for (i=0; i<SIZE;i++){
		for (g=0; g<SIZE;g++){
			putchar(canvas[g][i]);
		}
	}
}

void plotBinary(char binary[],int col,int startX, int startY){
        int x;
        int y;
        for (x=startX;x<SIZE;x++){
                for (y=startY;y<SIZE;y++){
                        plot(x,y,binary[y * SIZE + x]*col);
                }
        }
}


void clear(int col){
        int x;
        int y;
        for (x=0;x<SIZE;x++){
                for (y=0;y<SIZE;y++){
                        plot(x,y,col);
                }
        }
}

void main(){
	int rep;
	createCanvas(SIZE);
	for(;;){
		rep++;
		renderCanvas();
	}
}


